function GetAward(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/AwardMasterDetails/List`,"html",r,function(n){$("#divAwardTable").html("");$("#divAwardTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),localityCode:$("#txtCode").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetAward(parseInt(n),parseInt(currentPageSize),sortby);currentPageNumber=n}function onChangePageSize(n){GetAward(parseInt(currentPageNumber),parseInt(n),sortby);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortby=1;$(document).ready(function(){GetAward(currentPageNumber,currentPageSize,sortby)});$("#btnSearch").click(function(){GetAward(currentPageNumber,currentPageSize,sortby)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortby=1;GetAward(currentPageNumber,currentPageSize,sortby)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortby=2;GetAward(currentPageNumber,currentPageSize,sortby)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");GetAward(currentPageNumber,currentPageSize,sortby)});