function GetJarai(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/JaraiDetails/List`,"html",r,function(n){$("#divJaraiTable").html("");$("#divJaraiTable").html(n)})}function GetSearchParam(n,t,i){return{locality:$("#txtLoc").val(),khasra:$("#txtKhasra").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetJarai(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetJarai(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetJarai(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetJarai(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtLoc").val("");$("#txtKhasra").val("");GetJarai(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetJarai(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetJarai(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetJarai(currentPageNumber,currentPageSize,sortOrder)});