function GetMorLands(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/morLands/List`,"html",r,function(n){$("#divMorlandTable").html("");$("#divMorlandTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),propertyname:$("#txtProperty").val(),sitedesc:$("#txtDescription").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetMorLands(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetMorLands(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetMorLands(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetMorLands(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetMorLands(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetMorLands(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetMorLands(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtProperty").val("");$("#txtDescription").val("");GetMorLands(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetMorLands(currentPageNumber,currentPageSize,sortOrder)});