function resetCaptchaImage(){d=new Date;$("#img-captcha").attr("src","/get-captcha-image?"+d.getTime())}$("#btnlogin").click(function(){var n=document.getElementById("Password").value.trim(),t=document.getElementById("Username").value.trim();if(n=="")return alert("Please enter password."),!1;if(t=="")return alert("Please enter UserName."),!1;var i=document.getElementById("hdnvals").value.trim(),r=CryptoJS.enc.Utf8.parse(i),u=CryptoJS.enc.Utf8.parse(i),f=CryptoJS.AES.encrypt(CryptoJS.enc.Utf8.parse(n),r,{keySize:16,iv:u,mode:CryptoJS.mode.CBC,padding:CryptoJS.pad.Pkcs7}),e=CryptoJS.AES.encrypt(CryptoJS.enc.Utf8.parse(t),r,{keySize:16,iv:u,mode:CryptoJS.mode.CBC,padding:CryptoJS.pad.Pkcs7});document.getElementById("Password").value=f;document.getElementById("Username").value=e});$("#refreshcaptcha").click(function(){resetCaptchaImage()});