function GetDetails(n,t){var i=GetSearchParam(n,t,sortOrder);HttpPost(`/DMSFileUpload/List`,"html",i,function(n){$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){return{name:"test",departmentId:parseInt($("#DepartmentId").val()),localityId:parseInt($("#LocalityId").val()),KhasraId:parseInt($("#KhasraNoId").val()),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:t,pageNumber:n}}function onPaging(n){GetDetails(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDetails(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#DepartmentId").val("0").trigger("change");$("#LocalityId").val("0").trigger("change");$("#KhasraNoId").val("0").trigger("change");GetDetails(currentPageNumber,currentPageSize,sortOrder)});