function GetDetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/MonthlyRoster/List`,"html",r,function(n){$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){return{department:$("#txtDept").val(),zone:$("#txtZone").val(),division:$("#txtDiv").val(),locality:$("#txtLocality").val(),guard:$("#txtGuard").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDetails(parseInt(pageNumber),parseInt(currentPageSize),sortOrder);pageNumber=n}function onChangePageSize(n){GetDetails(parseInt(pageNumber),parseInt(n),sortOrder);currentPageSize=n}var pageNumber=1,currentPageSize=10,sortOrder=1;$(document).ready(function(){GetDetails(pageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDetails(pageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtDept").val("");$("#txtZone").val("");$("#txtDiv").val("");$("#txtLocality").val("");$("#txtGuard").val("");GetDetails(pageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDetails(pageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDetails(pageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetDetails(parseInt(pageNumber),parseInt(currentPageSize),sortOrder)});