function GetNavigateToGCP(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/NavigateToGCP/List`,"html",r,function(n){$("#divNavigateToGCP").html("");$("#divNavigateToGCP").html(n)})}function GetSearchParam(n,t,i){return{Zone:$("#txtZoneName").val(),village:$("#txtVillageName").val(),gisLabel:$("#textGCPLandMark").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetNavigateToGCP(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetNavigateToGCP(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetNavigateToGCP(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetNavigateToGCP(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetNavigateToGCP(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetNavigateToGCP(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtZoneName").val("");$("#txtVillageName").val("");$("#textGCPLandMark").val("");GetNavigateToGCP(currentPageNumber,currentPageSize,sortOrder)});