function GetBundle(n,t){var i=GetSearchParam(n,t);HttpPost(`/Bundle/List`,"html",i,function(n){$("#divBundleTable").html("");$("#divBundleTable").html(n)})}function GetSearchParam(n,t){return{name:"test",pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetBundle(parseInt(n),parseInt(currentPageSize));currentPageNumber=n}function onChangePageSize(n){GetBundle(parseInt(currentPageNumber),parseInt(n));currentPageSize=n}var currentPageNumber=1,currentPageSize=10;$(document).ready(function(){GetBundle(currentPageNumber,currentPageSize)});