function GetFileDetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/DisplayLabel/List`,"html",r,function(n){$("#LoadView").html("");$("#LoadView").html(n)})}function GetSearchParam(n,t,i){return{fileNo:$("#txtFileNo").val(),name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:t,pageNumber:n}}function onPaging(n){n=parseInt(n);GetFileDetails(currentPageNumber,currentPageSize,sortOrder);currentPageNumber=n}function onChangePageSize(n){n=parseInt(n);GetFileDetails(currentPageNumber,currentPageSize,sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(function(){GetFileDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetFileDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetFileDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetFileDetails(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetFileDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtFileNo").val("");$("#txtName").val("");GetFileDetails(currentPageNumber,currentPageSize,sortOrder)});