function GetAIdata(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/DDADecisionSupportSystem/List`,"html",r,function(n){$("#divAIChange").html("");$("#divAIChange").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtzone").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetAIdata(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetAIdata(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetAIdata(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetAIdata(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");GetDistrict(currentPageNumber,currentPageSize,sortOrder)});