function GetLandTransfer(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Planning/List`,"html",r,function(n){$("#divLandTransferTable").html("");$("#divLandTransferTable").html(n)})}function GetSearchParam(n,t,i){return{sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:t,pageNumber:n,unplannedname:$("#txtName").val(),plannedname:$("#txtUserName").val()}}function onPaging(n){GetLandTransfer(parseInt(n),currentPageSize,sortOrder);currentPageNumber=n}function onChangePageSize(n){GetLandTransfer(currentPageNumber,parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetLandTransfer(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetLandTransfer(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetLandTransfer(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetLandTransfer(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtUserName").val("");GetLandTransfer(currentPageNumber,currentPageSize,sortOrder)});