function GetCalculation(){var t=Number($("#tdtotalChallanAmount").text()),i=Number($("#tdtotalDemandAmount").text()),r=Number($("#TotalPayableInterest").text()),n=0;$(".calculation").find("tbody").find("tr").each(function(){n=n+Number($(this).find("#Amount").val())});t==""&&(t=0);i==""&&(i=0);r==""&&(r=0);n==""&&(n=0);document.getElementById("TotalPayable").value=i-t-n;document.getElementById("TotalDues").value=i+r-t-n}$("#Amount").change(function(){GetCalculation()});