function GetLeasedeed(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Leasedeed/List`,"html",r,function(n){$("#divLeasedeedTable").html("");$("#divLeasedeedTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtApplication").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetLeasedeed(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetLeasedeed(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetLeasedeed(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetLeasedeed(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtApplication").val("");GetLeasedeed(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetLeasedeed(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetLeasedeed(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetLeasedeed(currentPageNumber,currentPageSize,sortOrder)});