function GetAllotmentEntry(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/AllotmentEntry/List`,"html",r,function(n){$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){return{applicantname:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetAllotmentEntry(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetAllotmentEntry(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetAllotmentEntry(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetAllotmentEntry(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetAllotmentEntry(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetAllotmentEntry(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetAllotmentEntry(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetAllotmentEntry(currentPageNumber,currentPageSize,sortOrder)});