function GetDetailsHearingDetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/HearingDetails/ListHearingDetails`,"html",r,function(n){$("#divTable").html("");$("#divTable").html(n)})}function DateCheck(){var t=document.getElementById("txtHearingDate").value,n=new Date,i=String(n.getDate()).padStart(2,"0"),r=String(n.getMonth()+1).padStart(2,"0"),u=n.getFullYear(),f=u+"-"+r+"-"+i;t>f&&(document.getElementById("txtAttendee").hidden=!0,document.getElementById("txtRemark").hidden=!0,document.getElementById("file").hidden=!0)}function GetSearchParam(){var n=parseInt($("#RequestForProceedingId").val());return{refno:n,name:"test",sortBy:1,sortOrder:1,pageSize:100,pageNumber:1}}function onPaging(n){GetDetails(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDetails(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}function GetDetails(n){HttpGet(`/HearingDetails/RequestForProceedingEvictionView/?id=${n}`,"html",function(n){$("#RequestForProceedingEvictionDiv").html("");$("#RequestForProceedingEvictionDiv").html(n)})}function GetNoticeDetails(n){HttpGet(`/HearingDetails/NoticeGenerationView/?id=${n}`,"html",function(n){$("#NoticeGenerationDiv").html("");$("#NoticeGenerationDiv").html(n)})}function GetEvidenceDetails(n){HttpGet(`/HearingDetails/AllotteeEvidenceView/?id=${n}`,"html",function(n){$("#AllotteeEvidenceDiv").html("");$("#AllotteeEvidenceDiv").html(n)})}var currentPageNumber=1,currentPageSize=100,sortOrder=1;$(document).ready(function(){var n=parseInt($("#RequestForProceedingId").val());GetDetailsHearingDetails();GetDetails(n);GetNoticeDetails(n);GetEvidenceDetails(n);DateCheck()});