function GetInterestrate(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Interestrate/List`,"html",r,function(n){$("#divInterestrateTable").html("");$("#divInterestrateTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtProperty").val(),FromDate:$("#txtFromDate").val(),ToDate:$("#txtToDate").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetInterestrate(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetInterestrate(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetInterestrate(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetInterestrate(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtProperty").val("");$("#txtFromDate").val("");$("#txtToDate").val("");GetInterestrate(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetInterestrate(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetInterestrate(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetInterestrate(currentPageNumber,currentPageSize,sortOrder)});