function GetJudgement(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Judgement/List`,"html",r,function(n){$("#divJudgementTable").html("");$("#divJudgementTable").html(n)})}function GetSearchParam(n,t,i){return{letterReferenceNo:$("#txtReferenceNo").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetJudgement(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetJudgement(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetJudgement(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetJudgement(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtReferenceNo").val("");GetJudgement(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetJudgement(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetJudgement(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetJudgement(currentPageNumber,currentPageSize,sortOrder)});