function GetService(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/ApplyForServices/List`,"html",r,function(n){$("#divServiceTable").html("");$("#divServiceTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtService").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetService(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetService(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetService(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetService(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtService").val("");GetService(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetService(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetService(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetService(currentPageNumber,currentPageSize,sortOrder)});