function GetDetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Mortgage/List`,"html",r,function(n){console.log(n);$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){var r={refno:$("#txtRefNo").val(),name:$("#txtSocietyName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:t,pageNumber:n};return console.log(r),r}function onPaging(n){GetDetails(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDetails(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtRefNo").val("");$("#txtSocietyName").val("");GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDetails(currentPageNumber,currentPageSize,sortOrder)});