function GetNotifications(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Newlandnotificationdetails/List`,"html",r,function(n){$("#divNotificationsTable").html("");$("#divNotificationsTable").html(n)})}function GetSearchParam(n,t,i){return{type:$("#txttype").val(),notification:$("#txtnotification").val(),locality:$("#txtLoc").val(),khasra:$("#txtKhasra").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetNotifications(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetNotifications(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetNotifications(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetNotifications(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtnotification").val("");$("#txttype").val("");$("#txtLoc").val("");$("#txtKhasra").val("");GetNotifications(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetNotifications(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetNotifications(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetNotifications(currentPageNumber,currentPageSize,sortOrder)});