function GetExcel(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Request/RequestList`,"html",r,function(){var n=document.createElement("a");n.target="_blank";n.href="/Request/download";n.click()})}function GetDivision(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Request/List`,"html",r,function(n){$("#divScheme").html("");$("#divScheme").html(n)})}function GetSearchParam(n,t,i){var r={name:$("#txtName").val(),area:$("#txtCode").val(),fileno:$("#txtFileNo").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)};return console.log(r),r}function onPaging(n){GetDivision(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDivision(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btndownload").click(function(){GetExcel(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");$("#txtFileNo").val("");GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDivision(currentPageNumber,currentPageSize,sortOrder)});