function GetModuleCategory(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/ModuleCategory/List`,"html",r,function(n){$("#divModuleCategory").html("");$("#divModuleCategory").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetModuleCategory(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetModuleCategory(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetModuleCategory(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetModuleCategory(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetModuleCategory(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetModuleCategory(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetModuleCategory(currentPageNumber,currentPageSize,sortOrder)});