function onChange(n){HttpGet(`/Village/GetZoneList/?DepartmentId=${n}`,"json",function(n){for(var i='<option value="">Select<\/option>',t=0;t<n.length;t++)i=i+"<option value="+n[t].id+">"+n[t].name+"<\/option>";$("#ZoneId").select2("val","");$("#DivisionId").select2("val","");$("#ZoneId").html(i)})}function onChangeZone(n){HttpGet(`/Village/GetDivisionList/?ZoneId=${n}`,"json",function(n){for(var i='<option value="">Select<\/option>',t=0;t<n.length;t++)i=i+"<option value="+n[t].id+">"+n[t].name+"<\/option>";$("#DivisionId").select2("val","");$("#DivisionId").html(i)})}