function GetActions(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/actions/List`,"html",r,function(n){$("#divActionsTable").html("");$("#divActionsTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),icon:$("#txtIcon").val(),color:$("#txtColor").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetActions(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetActions(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetActions(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetActions(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetActions(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetActions(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtColor").val("");$("#txtIcon").val("");GetActions(currentPageNumber,currentPageSize,sortOrder)});