function GetApplicationNotification(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/ApplicationNotificationTemplate/List`,"html",r,function(n){$("#divApplicationNotificationTemplateTable").html("");$("#divApplicationNotificationTemplateTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetApplicationNotification(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetApplicationNotification(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetApplicationNotification(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetApplicationNotification(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetApplicationNotification(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetApplicationNotification(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetApplicationNotification(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetApplicationNotification(currentPageNumber,currentPageSize,sortOrder)});