function GetApprovalStatus(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/approvalstatus/List`,"html",r,function(n){$("#divApprovalstatusTable").html("");$("#divApprovalstatusTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetApprovalStatus(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetApprovalStatus(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetApprovalStatus(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetApprovalStatus(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetApprovalStatus(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetApprovalStatus(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetApprovalStatus(currentPageNumber,currentPageSize,sortOrder)});