function GetCourt(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Court/List`,"html",r,function(n){$("#divCourtTable").html("");$("#divCourtTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),address:$("#txtAddress").val(),phoneno:$("#txtPhoneno").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetCourt(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetCourt(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetCourt(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetCourt(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtAddress").val("");$("#txtPhoneno").val("");GetCourt(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetCourt(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetCourt(currentPageNumber,currentPageSize,sortOrder)});