function GetDemolitionchecklist(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Demolitionchecklist/List`,"html",r,function(n){$("#divDemolitionchecklistTable").html("");$("#divDemolitionchecklistTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDemolitionchecklist(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDemolitionchecklist(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDemolitionchecklist(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDemolitionchecklist(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetDemolitionchecklist(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDemolitionchecklist(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDemolitionchecklist(currentPageNumber,currentPageSize,sortOrder)});