function GetDemolitiondocument(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Demolitiondocument/List`,"html",r,function(n){$("#divDemolitiondocumentTable").html("");$("#divDemolitiondocumentTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDemolitiondocument(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDemolitiondocument(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDemolitiondocument(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDemolitiondocument(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDemolitiondocument(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDemolitiondocument(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetDemolitiondocument(currentPageNumber,currentPageSize,sortOrder)});