function GetDepartment(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/department/List`,"html",r,function(n){$("#divDepartmentTable").html("");$("#divDepartmentTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDepartment(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDepartment(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDepartment(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDepartment(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDepartment(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDepartment(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetDepartment(currentPageNumber,currentPageSize,sortOrder)});