function GetDepartmentTarget(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/departmenttarget/List`,"html",r,function(n){$("#divDepartmentTargetTable").html("");$("#divDepartmentTargetTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function Validate(){var n=document.getElementById("txtFiles").value,t=document.getElementById("txtWeekFiles").value;parseInt(n)<=parseInt(t)&&($("#txtWeekFiles").val(""),alert("Weekly Files to be done should be less than Files to be done !!"))}function onPaging(n){GetDepartmentTarget(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDepartmentTarget(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDepartmentTarget(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDepartmentTarget(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDepartmentTarget(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDepartmentTarget(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetDepartmentTarget(currentPageNumber,currentPageSize,sortOrder)});