function GetDistrict(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/district/List`,"html",r,function(n){$("#divDistrict").html("");$("#divDistrict").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),code:$("#txtCode").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDistrict(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDistrict(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}function DepartmentMessage(){var n=$("#Name").val();return n==""?"Department Name is Mandatory Field":""}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");GetDistrict(currentPageNumber,currentPageSize,sortOrder)});$("#myForm").validate({rules:{Name:{required:!0}},messages:{Name:{required:DepartmentMessage}},highlight:function(n){$(n).closest(".form-group").addClass("has-error")},unhighlight:function(n){$(n).closest(".form-group").removeClass("has-error")},errorElement:"span",errorClass:"help-block",errorPlacement:function(n,t){t.parent(".input-group").length?n.insertAfter(t.parent()):n.insertAfter(t)},submitHandler:function(){return!0}});