function GetDivision(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/division/List`,"html",r,function(n){$("#divDivisionTable").html("");$("#divDivisionTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),code:$("#txtCode").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDivision(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDivision(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDivision(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDivision(currentPageNumber,currentPageSize,sortOrder)});