function GetLocality(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/locality/List`,"html",r,function(n){$("#divLocalityTable").html("");$("#divLocalityTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),localityCode:$("#txtCode").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetLocality(parseInt(n),parseInt(currentPageSize),sortby);currentPageNumber=n}function onChangePageSize(n){GetLocality(parseInt(currentPageNumber),parseInt(n),sortby);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortby=1;$(document).ready(function(){GetLocality(currentPageNumber,currentPageSize,sortby)});$("#btnSearch").click(function(){GetLocality(currentPageNumber,currentPageSize,sortby)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortby=1;GetLocality(currentPageNumber,currentPageSize,sortby)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortby=2;GetLocality(currentPageNumber,currentPageSize,sortby)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");GetLocality(currentPageNumber,currentPageSize,sortby)});