function GetLog(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Log/List`,"html",r,function(n){$("#divAcquiredLandVillage").html("");$("#divAcquiredLandVillage").html(n)})}function GetSearchParam(n,t,i){return{application:$("#txtName").val(),traceId:$("#txtTrace").val(),date:$("#txtDate").val(),logger:$("#txtLogger").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetLog(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetLog(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetLog(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetLog(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtTrace").val("");$("#txtDate").val("");$("#txtLogger").val("");GetLog(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetLog(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetLog(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetLog(currentPageNumber,currentPageSize,sortOrder)});