function GetMenu(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/menu/List`,"html",r,function(n){$("#divMenuTable").html("");$("#divMenuTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtMenuName").val(),moduleName:$("#txtName").val(),parentname:$("#txtParent").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetMenu(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetMenu(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetMenu(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetMenu(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetMenu(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetMenu(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetMenu(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtMenuName").val("");$("#txtName").val("");$("#txtParent").val("");GetMenu(currentPageNumber,currentPageSize,sortOrder)});