function GetModule(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Module/List`,"html",r,function(n){$("#divModuleTable").html("");$("#divModuleTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),description:$("#txtDescription").val(),url:$("#txtUrl").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetModule(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetModule(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetModule(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetModule(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtDescription").val("");$("#txtUrl").val("");GetModule(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetModule(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetModule(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetModule(currentPageNumber,currentPageSize,sortOrder)});