function GetAcquiredLandVillage(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Village/List`,"html",r,function(n){$("#divAcquiredLandVillage").html("");$("#divAcquiredLandVillage").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),tehsil:$("#txtTehsil").val(),district:$("#txtDistrict").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetAcquiredLandVillage(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetAcquiredLandVillage(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetAcquiredLandVillage(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetAcquiredLandVillage(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtTehsil").val("");$("#txtDistrict").val("");GetAcquiredLandVillage(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetAcquiredLandVillage(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetAcquiredLandVillage(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetAcquiredLandVillage(currentPageNumber,currentPageSize,sortOrder)});