function GetProposaldetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/proposalDetails/List`,"html",r,function(n){$("#divProposaldetailsTable").html("");$("#divProposaldetailsTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),requiredAgency:$("#txtBody").val(),proposalFileNo:$("#txtFile").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetProposaldetails(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetProposaldetails(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetProposaldetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetProposaldetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtBody").val("");$("#txtFile").val("");GetProposaldetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetProposaldetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetProposaldetails(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetProposaldetails(currentPageNumber,currentPageSize,sortOrder)});