function GetDetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Rebate/List`,"html",r,function(n){$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){return{RebateOn:$("#txtRebateOn").val(),FromDate:$("#txtFromDate").val(),ToDate:$("#txtToDate").val(),RebatePercentage:$("#txtPercentage").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:t,pageNumber:n}}function onPaging(n){n=parseInt(n);GetDetails(n,currentPageSize,sortOrder);currentPageNumber=n}function onChangePageSize(n){n=parseInt(n);GetDetails(currentPageNumber,n,sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtRebateOn").val("");$("#txtFromDate").val("");$("#txtToDate").val("");$("#txtPercentage").val("");GetDetails(currentPageNumber,currentPageSize,sortOrder)});