function GetRole(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/role/List`,"html",r,function(n){$("#divRoleTable").html("");$("#divRoleTable").html(n)})}function GetSearchParam(n,t,i){return{Name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetRole(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetRole(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetRole(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetRole(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetRole(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetRole(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetRole(currentPageNumber,currentPageSize,sortOrder)});