function GetSchemeFileLoading(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/schemefileloading/List`,"html",r,function(n){$("#divSchemeFileTable").html("");$("#divSchemeFileTable").html(n)})}function GetSearchParam(n,t,i){return{schemename:$("#txtName").val(),schemecode:$("#txtCode").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetSchemeFileLoading(parseInt(n),parseInt(currentPageSize),sortby);currentPageNumber=n}function onChangePageSize(n){GetSchemeFileLoading(parseInt(currentPageNumber),parseInt(n),sortby);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortby=1;$(document).ready(function(){GetSchemeFileLoading(currentPageNumber,currentPageSize,sortby)});$("#btnSearch").click(function(){GetSchemeFileLoading(currentPageNumber,currentPageSize,sortby)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortby=1;GetSchemeFileLoading(currentPageNumber,currentPageSize,sortby)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortby=2;GetSchemeFileLoading(currentPageNumber,currentPageSize,sortby)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtCode").val("");GetSchemeFileLoading(currentPageNumber,currentPageSize,sortby)});