function GetStructure(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Structure/List`,"html",r,function(n){$("#divStructureTable").html("");$("#divStructureTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetStructure(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetStructure(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetStructure(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetStructure(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetStructure(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetStructure(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetStructure(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetStructure(currentPageNumber,currentPageSize,sortOrder)});