function GetUser(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Surveyuserdetail/List`,"html",r,function(n){$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetUser(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetUser(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetUser(currentPageNumber,currentPageSize,sortOrder)});