function GetTehsil(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/Tehsil/List`,"html",r,function(n){$("#divTehsilTable").html("");$("#divTehsilTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetTehsil(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetTehsil(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetTehsil(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetTehsil(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtName").val("");GetTehsil(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetTehsil(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetTehsil(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetTehsil(currentPageNumber,currentPageSize,sortOrder)});