function GetUser(n,t,i){var r=GetSearchParam(parseInt(n),parseInt(t),i);HttpPost(`/usermanagement/List`,"html",r,function(n){$("#divUser").html("");$("#divUser").html(n)})}function GetSearchParam(n,t,i){return{userName:$("#txtUserName").val(),name:$("#txtName").val(),phoneNumber:$("#txtPhoneNumber").val(),email:$("#txtEmail").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetUser(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetUser(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#txtUserName").val("");$("#txtName").val("");$("#txtPhoneNumber").val("");$("#txtEmail").val("");GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetUser(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetUser(currentPageNumber,currentPageSize,sortOrder)});