function GetVillage(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/village/List`,"html",r,function(n){$("#divVillageTable").html("");$("#divVillageTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),zone:$("#txtZoneName").val(),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetVillage(parseInt(n),parseInt(currentPageSize),sortby);currentPageNumber=n}function onChangePageSize(n){GetVillage(parseInt(currentPageNumber),parseInt(n),sortby);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortby=1;$(document).ready(function(){GetVillage(currentPageNumber,currentPageSize,sortby)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortby=1;GetVillage(currentPageNumber,currentPageSize,sortby)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortby=2;GetVillage(currentPageNumber,currentPageSize,sortby)});$("#btnSearch").click(function(){GetVillage(currentPageNumber,currentPageSize,sortby)});$("#btnReset").click(function(){$("#txtName").val("");$("#txtZoneName").val("");GetVillage(currentPageNumber,currentPageSize,sortby)});