function GetDetails(n,t,i){var r=GetSearchParam(n,t,i);HttpPost(`/WorkFlowTemplate/List`,"html",r,function(n){$("#divTable").html("");$("#divTable").html(n)})}function GetSearchParam(n,t,i){return{name:$("#txtName").val(),version:$("#txtVersion").val(),moduleid:parseInt($("#ddlModuleId option:selected").val()),sortBy:$("#ddlSort").children("option:selected").val(),sortOrder:parseInt(i),pageSize:parseInt(t),pageNumber:parseInt(n)}}function onPaging(n){GetDetails(parseInt(n),parseInt(currentPageSize),sortOrder);currentPageNumber=n}function onChangePageSize(n){GetDetails(parseInt(currentPageNumber),parseInt(n),sortOrder);currentPageSize=n}var currentPageNumber=1,currentPageSize=5,sortOrder=1;$(document).ready(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnSearch").click(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#ddlSort").change(function(){GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnReset").click(function(){$("#ddlModuleId").val(0).trigger("change");$("#txtName").val("");$("#txtVersion").val("");GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnAscending").click(function(){$("#btnDescending").removeClass("active");$("#btnAscending").addClass("active");sortOrder=1;GetDetails(currentPageNumber,currentPageSize,sortOrder)});$("#btnDescending").click(function(){$("#btnAscending").removeClass("active");$("#btnDescending").addClass("active");sortOrder=2;GetDetails(currentPageNumber,currentPageSize,sortOrder)});